import React from "react";
import * as ROSLIB from "roslib"
import './test.css'
import ListItem from "./listItem";

const TestRosBridge = () => {

    const ros = new ROSLIB.Ros();
    ros.connect('ws://localhost:9090', 'echo-protocol');

    const rosTopic = new ROSLIB.Topic({
		ros : ros,
		name : '/turtle1/start',
		messageType : 'turtle_test_suite_msgs/Start'
	});

	const start = new ROSLIB.Message({
		data : 1
	});

    const sendStart = () => {
        console.log("sendStart", rosTopic, start);
		rosTopic.publish(start);
    }

    function getTopics() {
        var topicsClient = new ROSLIB.Service({
        ros : ros,
        name : '/rosapi/topics',
        serviceType : 'rosapi/Topics'
        });

        var request = new ROSLIB.ServiceRequest();

        topicsClient.callService(request, function(result) {
        console.log("Getting topics...");
        console.log(result.topics);
        });
    };


    return(
        <div className="tur">
            <h1>TurtleTestSuite</h1>
            <button onClick={sendStart}>1m 갔다가 복귀</button>  
            <button>360° 회전</button>  
            <button>범퍼</button>  
            <button>비상정지 버튼</button>  
            <button>LCDirector</button>  
            <button onClick={getTopics}>Get Topics</button>
        </div>
    )
}

export default TestRosBridge;
