    // });

    // var scan = new ROSLIB.Topic({
    //     ros : ros,
    //     name : '/scan',
    //     messageType : 'sensor_msgs/LaserScan'
    // });

    const [pauseData, setPauseData] = useState(false);

    const [pause,] = useState(new ROSLIB.Topic({
        ros : ros,
        name : '/pause',
        messageType : 'std_msgs/Bool'
    }));

    useEffect(() => {
        pause.subscribe((m) =>{
            console.log(m);
            setPauseData(m.data);
        })
    });

    return( 
        <div>
            <h1>Pause Data : {pauseData ? "True" : "False"} </h1>
        </div>
    )
}

export default BlodyBeta2TestSuite;
