import React, {Component} from "react";

class ScrollBox extends Component {
    render() {
        const style = {
            border: '1px solid black',
            height: '300px',
            width: '300px',
            overflow: 'auto',
            position: 'relative'
        };


        const innerStyle = {
            height: '650px',
            width: '100%',
            background: 'linear-gradient(red, blue)'
        }

        return (
            <div 
                style={style}
                ref={(ref) => this.box=ref}>
                <div style={innerStyle}/>
            </div>
        )
    }
}

export default ScrollBox;
