import React, { useState } from "react";
import ROSLIB from "roslib";
import ros from '../../config/ros-config';

const BlodyBeta2TestSuite = () => {

    // var bummper = new ROSLIB.Topic({
    //     ros : ros,
    //     name : '/pause',
    //     messageType : 'std_msgs/bool'
    // });

    // var emergency = new ROSLIB.Topic({
    //     ros : ros,
    //     name : '/emergency',
    //     messageType : 'std_msgs/Bool'
    // });

    // var safetyLantern = new ROSLIB.Topic({
    //     ros : ros,
    //     name : '/uros/safety_lantern',
    //     messageType : 'std_msgs/Bool'
    // });

    // var battery = new ROSLIB.Topic({
    //     ros : ros,
    //     name : '/battery/voltage',
    //     messageType : 'std_msgs/Float32'
    // });

    // var imu = new ROSLIB.Topic({
    //     ros : ros,
    //     name : '/imu',
    //     messageType : 'sensor_msgs/Imu'
    // });

    // var scan = new ROSLIB.Topic({
    //     ros : ros,
    //     name : '/scan',
    //     messageType : 'sensor_msgs/LaserScan'
    // });

    const [pause,] = useState(new ROSLIB.Topic({
        ros : ros,
        name : '/pause',
        messageType : 'std_msgs/Bool'
    }));

    const [is_sub, setIsSub] = useState(false);

    function getSub() {

        if(is_sub){
            pause.subscribe((mesaage) => {
                console.log(mesaage);
                pause.unsubscribe();
            });
        } else {
            pause.unsubscribe();
        }
        setIsSub(!is_sub);
    }

    return( 
        <div>
            <button onClick={getSub}>안녕하세요</button>
        </div>
    )
}

export default BlodyBeta2TestSuite;
